sp_widget.client_script:
function controller ($rootScope) {
	/* exported controller */
	/* global console */
	var c = this;
	this.selectItem = function (idx) {
		var id = this.data.notes[idx].sys_id;
		// eslint-disable-next-line no-console
		console.log("Note ID: " + id);
		$rootScope.noteID = id;
		$rootScope.$emit("selectNote", id);
	};
	$rootScope.$on("updateList", function (event, data) {
		var modifiedNode = c.data.notes.find(function (note) {
			return note.sys_id === data.noteID;
		});
		modifiedNode.title = data.title;
		modifiedNode.note = data.note;
	});
}

sp_widget.link:
function link (/*scope, element, attrs, controller*/) {
	/* exported link */
}

sp_widget.script:
(function () {
	/* global GlideRecord, gs, data, $sp */
	//create an array to populate with notes
	data.notes = [];
	var noteGR = new GlideRecord("x_snc_createnotes_note");
	noteGR.addQuery("user", gs.getUser().getID());
	noteGR.orderByDesc("sys_created_on");
	noteGR.query();
	while (noteGR.next()) {
		var noteObj = {};
		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(noteObj, noteGR, "number,title,sys_id");
		//get the first 20 characters of the description
		noteObj.note = noteGR.getValue("note").slice(0, 20);
		//push the populated obj into the array
		data.notes.push(noteObj);
	}
})();
